                    var whiskerContainer = svg
                       .append("g")
                       .attr("class", "whisker-container")
                       .attr("transform", "translate(" + LEFT_MARGIN_TO_CHART + ", " + (BRICK_MAX_HEIGHT + (scope.showScale ? 35: 15) + BRICK_MARGIN_TOP + 8) + ")")

                        var yAxis = d3.svg
