                function fromUser(text) {
                    return text;
                }

                function toUser(text) {
                    // Don't silently replace empty by zero !
                    if(text!=null && text!=undefined && text!=='') {
                        return parseFloat(text);
                    } else return '';
                }
                ngModel.$parsers.push(fromUser);
